mapper.select:
select count(1) FROM up_patch p INNER JOIN up_project pj ON p.project_id = pj.project_id and pj.del_flag = 'N' INNER JOIN up_product pd ON p.product_id = pd.product_id and pd.del_flag = 'N' INNER JOIN sys_user su ON p.update_by = su.user_name and su.del_flag = '0' and su.status = '0' INNER JOIN sys_dept sd ON sd.dept_id = su.dept_id
and patch_status = #{upPatch.patchStatus}
AND p.build_time < date_format(#{upPatch.createTime},'%Y-%m-%d %H:%i')
and p.del_flag != 'Y' and sd.parent_id = #{deptId}

SELECT su.login_name AS id, p.jira_no as jiraNo, p.update_by as username, p.merge_package_flag as merge, p.patch_status as status, p.patch_file_name as patchName FROM up_patch p INNER JOIN up_project pj ON p.project_id = pj.project_id and pj.del_flag = 'N' INNER JOIN up_product pd ON p.product_id = pd.product_id and pd.del_flag = 'N' INNER JOIN sys_user su ON p.update_by = su.user_name and su.del_flag = '0' and su.status = '0' INNER JOIN sys_dept sd ON sd.dept_id = su.dept_id where p.del_flag != 'Y' and p.patch_status in
#{status}
and sd.parent_id = #{deptId} and pd.product_id != #{productId} UNION ALL SELECT su.login_name AS id, p.jira_no AS jiraNo, p.update_by AS username, p.merge_package_flag AS MERGE, p.patch_status AS STATUS, p.patch_file_name AS patchName FROM up_patch p INNER JOIN up_project pj ON p.project_id = pj.project_id AND pj.del_flag = 'N' INNER JOIN up_product pd ON p.product_id = pd.product_id AND pd.del_flag = 'N' INNER JOIN sys_user su ON p.update_by = su.user_name AND su.del_flag = '0' AND su.status = '0' INNER JOIN sys_dept sd ON sd.dept_id = su.dept_id WHERE p.del_flag != 'Y' AND p.patch_status IN
#{status}
AND sd.parent_id = #{deptId} AND pd.product_id = #{productId} GROUP BY p.jira_no

SELECT u.jira_no jira_no,u.topic topic,u.demand_no demand_no,d.dict_value dict_value,d.dict_label dict_label,u.update_by update_by,u.build_time build_time FROM up_patch u inner join sys_dict_data d on u.patch_status = d.dict_value and d.dict_type = 'up_patch_status'

    u.product_id = #{productId}
     and u.project_id = #{projectId}
    and
    (
        u.jira_no = #{jiraNo}
         or u.demand_no = #{demandNo}
    )

and del_flag != 'Y'

